  {name: "Kian 4300 Sectional", category: "seating", description: nil, image: "http://thefashionfurniture.com/media/catalog/product/cache/1/thumbnail/9df78eab33525d08d6e5fb8d27136e95/4/3/4300_kian_c4300-ok-01.jpg", pieces: nil, style: "contemporary", counter_height: nil, sectional: nil, sofa_love: true, price: 0, brand_id: 10, motion: true, images: nil},
  {name: "Kian 4400 Sectional", category: "seating", description: nil, image: "https://www.wyckes.com/images/products/4400.jpg", pieces: nil, style: "contemporary", counter_height: nil, sectional: nil, sofa_love: true, price: 0, brand_id: 10, motion: true, images: nil},
  {name: "Pecos Sofa Set", category: "seating", description: "The Pecos Collection blends style and function to create a platform for relaxation. Modernizing the look of the ample seating is an inset contrast welt. Chair, love seat and sofa are available in both manual and power version while the sectional is available in manual version only. With a pull of the handle, the user is changed from a seated position to a reclined position smoothly. The leather gel match covered collection is offered in three stylish colors – gray, red and dark brown.", image: "https://www.homelegance.com/wp-content/uploads/2017/06/8480BRW-3__1-750x500.jpg", pieces: nil, style: "contemporary", counter_height: nil, sectional: false, sofa_love: true, price: 1099, brand_id: 2, motion: true, images: nil},
  {name: "Gartel Day Bed", category: "youth", description: "Throw the ultimate slumber party with this espresso finished 3-layer nesting daybed! Have friends or family comfortably tucked in any of the three twin-size sleeping spaces. When it comes time for them to leave, roll the frames back under the daybed and regain valuable floor space. The slatted ends and plank style sides give a charming visual, adding a touch of style to a piece with tons of function.", image: "https://www.foagroup.com/media/catalog/product/cache/1/image/040ec09b1e35df139433887a97daa66f/c/m/cm1610.jpg", pieces: 1, style: "contemporary", counter_height: nil, sectional: nil, sofa_love: nil, price: 0, brand_id: 4, motion: nil, images: nil},
